export class HttpHeadersInjector {
  constructor() {
  }
  public injectCorsHeaders(current_headers:{}) {
    current_headers['allow-origin'] = true;
  }
}

let headers = {}
let hh = new HttpHeadersInjector();
hh.injectCorsHeaders(headers);
console.log(headers);

